var _ = require('underscore');
var util = require('util');
var path = require('path');
var fs = require('fs');
var _DEBUG = false;
var article_dir = require('./article_dir');

/* ************************************
 * 
 * ************************************ */

/* ******* CLOSURE ********* */

/* ********* EXPORTS ******** */

module.exports = function (folder, cb) {

	console.log('adding folder %s', folder);
	this._folders =  null;
	var new_folder_path = path.resolve(article_dir(), folder);

	fs.exists(new_folder_path, function(ex){

		if (ex) {
			return cb(new Error('folder already exists'));
		}

		fs.mkdir(new_folder_path, cb);

	});
}; // end export function