var _ = require('underscore');
var util = require('util');
var path = require('path');
var fs = require('fs');
var _DEBUG = false;
var marked = require('marked');
var hyperlink = require('./hyperlink_marked');

/* ********* EXPORTS ******** */

module.exports = function (article, context) {

	console.log('getting article content for %s', util.inspect(article));

	var content = hyperlink(_.isString(article) ? article : article.content, context);
	if (!content) return '';

	return marked(content);
}; // end export function